simple_info:
SELECT ?o
WHERE 
{
  wd:%s schema:description ?o.
  FILTER ( lang(?o) = "en" )
} LIMIT 50

relation_filter:
SELECT ?o ?x
WHERE 
{
   ?o wdt:P31  wd:%s .
   ?o wdt:%s wd:%s . 

} LIMIT 50

batch_restriction_query:
SELECT ?oLabel  ?v ?value ?unitLabel
WHERE
{
   ?o wdt:P31  wd:%s .
   ?o wdt:%s ?v .
  OPTIONAL {
   ?o  p:%s/psv:%s ?v .
   ?v     wikibase:quantityAmount     ?value.
   ?v     wikibase:quantityUnit       ?unit.
}
   SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
      } LIMIT 50

batch_restriction_numerical_query:
SELECT ?o ?v
WHERE
  {
    ?o wdt:P31 wd:%s .
    ?o wdt:%s ?v .
    FILTER (?v %s %s)
   SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
 } LIMIT 50
