package uk.ac.ceb.como.jaxb.generate.xml.test;

import org.junit.Test;

import uk.ac.cam.ceb.como.io.chem.file.jaxb.Module;

import javax.xml.bind.*;

// TODO: Auto-generated Javadoc
/**
 * The Class GenerateRootElementTest.
 *
 * @author nk510
 * @version 0.0.1
 * 
 *          This class tests generating root CompChem XML tag by using jaxb classes
 *          generated by using CompChem XML Schema
 */

public class GenerateRootElementTest {

	/**
	 * Generate root element.
	 *
	 * @author nk510
	 * @throws JAXBException         <p> Method generates root tag element in CompChem XML by using JAXB
	 *             classes built based on CompChem XML Schema. Package-info.java
	 *             contains definition of all namespaces used in root element. </p>
	 */

	@Test
	public void generateRootElement() throws JAXBException {

		JAXBContext jc = JAXBContext.newInstance(Module.class);

		Module rootElement = new Module();

		Marshaller marshaller = jc.createMarshaller();
		marshaller.setProperty(Marshaller.JAXB_FORMATTED_OUTPUT, true);
		marshaller.marshal(rootElement, System.out);

	}

}